// Step 1: Install the HYELLO Payment Widget
npm install @hyello/payment-widget

// Step 2: Import the component
import { ReactCheckout } from '@hyello/payment-widget';
import '@hyello/payment-widget/dist/hyello.css';

// Step 3: Use in your React component
function PaymentPage() {
  const config = {
    apiKey: 'YOUR_API_KEY_HERE',  // Get from API Keys page
    environment: 'sandbox',        // Use 'production' when ready
    theme: 'dark',
    primaryColor: '#ffd700',
    borderRadius: 'medium',

  };

  const paymentData = {
    amount: 29.99,
    currency: 'USD',
    description: 'Product Purchase',

  };

  const handleSuccess = (result) => {
    console.log('Payment successful!', result);
    // Redirect to success page or show confirmation
    alert(`Payment completed: ${result.amount} ${result.currency}`);
  };

  const handleError = (error) => {
    console.error('Payment failed:', error);
    alert('Payment failed: ' + error.message);
  };

  return (
    <ReactCheckout
      config={config}
      paymentData={paymentData}
      onSuccess={handleSuccess}
      onError={handleError}
    />
  );
}

export default PaymentPage;

